function setUniform(job, playerPed)
	TriggerEvent('skinchanger:getSkin', function(skin)
		if skin.sex == 0 then
			if Config.Uniforms[job].male then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms[job].male)
			else
				ESX.ShowNotification(_U('no_outfit'))
			end

			if job == 'bullet_wear' then
				TriggerEvent('Lorraxs:checkArmour',100)
				SetPedArmour(playerPed, 100)

			end
		else
			if Config.Uniforms[job].female then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms[job].female)
			else
				ESX.ShowNotification(_U('no_outfit'))
			end

			if job == 'bullet_wear' then
				TriggerEvent('Lorraxs:checkArmour',100)
				SetPedArmour(playerPed, 100)
			end
		end
	end)
end
function OpenArmoryMenu(station)

  if Config.EnableArmoryManagement then
    --if PlayerData.job.grade_name == 'capo' or or PlayerData.job.grade_name == 'consigliere' or PlayerData.job.grade_name == 'boss' then
     local elements = { 
      --[[ table.insert(elements, {label = _U('get_weapon'),  value = 'get_weapon'})
     table.insert(elements, {label = _U('put_weapon'), value = 'put_weapon'})
     table.insert(elements, {label = _U('put_stock'), value = 'put_stock'})
     table.insert(elements, {label = _U('get_stock'), value = 'get_stock'})]]
      --[[ {label = _U('get_weapon'), value = 'get_weapon'},
      {label = _U('put_weapon'), value = 'put_weapon'},
      {label = "Gửi đồ", value = 'put_stock'},
      {label = "Lấy đồ", value = 'get_stock'}, ]]
    } 
    table.insert(elements, {label = "Gửi đồ", value = 'put_stock'})
    if PlayerData.job.grade_name == 'capo' or PlayerData.job.grade_name == 'consigliere' or PlayerData.job.grade_name == 'boss' then
     table.insert(elements, {label = "Lấy đồ", value = 'get_stock'})
    end

    ESX.UI.Menu.CloseAll()

    ESX.UI.Menu.Open(
      'default', GetCurrentResourceName(), 'armory',
      {
        title    = _U('armory'),
        align    = 'bottom-right',
        elements = elements,
      },
      function(data, menu)

        if data.current.value == 'get_weapon' then
          OpenGetWeaponMenu()
        end

        if data.current.value == 'put_weapon' then
          OpenPutWeaponMenu()
        end

        if data.current.value == 'buy_weapons' then
          OpenBuyWeaponsMenu(station)
        end

       if data.current.value == 'put_stock' then
        OpenPutStocksMenu()
      end

        if data.current.value == 'get_stock' then
              OpenGetStocksMenu()
            end

      end,
      function(data, menu)

        menu.close()

        CurrentAction     = 'menu_armory'
        CurrentActionMsg  = _U('open_armory')
        CurrentActionData = {station = station}
      end
    )

  else

    local elements = {}

    for i=1, #Config.MafiaStations[station].AuthorizedWeapons, 1 do
      local weapon = Config.MafiaStations[station].AuthorizedWeapons[i]
      table.insert(elements, {label = ESX.GetWeaponLabel(weapon.name), value = weapon.name})
    end

    ESX.UI.Menu.CloseAll()

    ESX.UI.Menu.Open(
      'default', GetCurrentResourceName(), 'armory',
      {
        title    = _U('armory'),
        align    = 'bottom-right',
        elements = elements,
      },
      function(data, menu)
        local weapon = data.current.value
        TriggerServerEvent('esx_jobg:giveWeapon', weapon,  50)
      end,
      function(data, menu)

        menu.close()

        CurrentAction     = 'menu_armory'
        CurrentActionMsg  = _U('open_armory')
        CurrentActionData = {station = station}

      end
    )

  end

end

function OpenPutStocksMenu()
	ESX.TriggerServerCallback('esx_jobg:getPlayerInventory', function(inventory)
		local elements = {}

		for i=1, #inventory.items, 1 do
			local item = inventory.items[i]

			if item.count > 0 then
				table.insert(elements, {
					label = item.label .. ' x' .. item.count,
					type = 'item_standard',
					value = item.name
				})
			end
		end

		ESX.UI.Menu.Open('default', GetCurrentResourceName(), 'stocks_menu', {
			title    = _U('inventory'),
			align    = 'bottom-right',
			elements = elements
		}, function(data, menu)
			local itemName = data.current.value

			ESX.UI.Menu.Open('dialog', GetCurrentResourceName(), 'stocks_menu_put_item_count', {
				title = _U('quantity')
			}, function(data2, menu2)
				local count = tonumber(data2.value)

				if count == nil then
					ESX.ShowNotification(_U('quantity_invalid'))
				else
					menu2.close()
					menu.close()
					TriggerEvent('esx_jobg:putStockItems', itemName, count)

					Citizen.Wait(300)
					OpenPutStocksMenu()
				end
			end, function(data2, menu2)
				menu2.close()
			end)
		end, function(data, menu)
			menu.close()
		end)
	end)
end

