function OpenGetWeaponMenu()

  ESX.TriggerServerCallback('esx_jobg:getArmoryWeapons', function(weapons)

    local elements = {}

    for i=1, #weapons, 1 do
      if weapons[i].count > 0 then
        table.insert(elements, {label = 'x' .. weapons[i].count .. ' ' .. ESX.GetWeaponLabel(weapons[i].name), value = weapons[i].name})
      end
    end

    ESX.UI.Menu.Open(
      'default', GetCurrentResourceName(), 'armory_get_weapon',
      {
        title    = _U('get_weapon_menu'),
        align    = 'bottom-right',
        elements = elements,
      },
      function(data, menu)

        menu.close()

        ESX.TriggerServerCallback('esx_jobg:removeArmoryWeapon', function()
          OpenGetWeaponMenu()
        end, data.current.value)

      end,
      function(data, menu)
        menu.close()
      end
    )

  end)

end

function OpenPutWeaponMenu()

  local elements   = {}
  local playerPed  = GetPlayerPed(-1)
  local weaponList = ESX.GetWeaponList()

  for i=1, #weaponList, 1 do

    local weaponHash = GetHashKey(weaponList[i].name)

    if HasPedGotWeapon(playerPed,  weaponHash,  false) and weaponList[i].name ~= 'WEAPON_UNARMED' 
    and weaponList[i].name ~= "WEAPON_FLARE"
    and weaponList[i].name ~= "WEAPON_REVOLVER"
    and weaponList[i].name ~= "WEAPON_VINTAGEPISTOL"
    and weaponList[i].name ~= "WEAPON_MARKSMANPISTOL"
    and weaponList[i].name ~= "WEAPON_ASSAULTSMG"
    and weaponList[i].name ~= "WEAPON_COMBATMG"
    and weaponList[i].name ~= "WEAPON_GUSENBERG"
    and weaponList[i].name ~= "WEAPON_MACHINEPISTOL"
    and weaponList[i].name ~= "WEAPON_BULLPUPRIFLE"
    and weaponList[i].name ~= "WEAPON_ASSAULTSHOTGUN"
    and weaponList[i].name ~= "WEAPON_MUSKET"
    and weaponList[i].name ~= "WEAPON_SNIPERRIFLE"
    and weaponList[i].name ~= "WEAPON_KNIFE"
    and weaponList[i].name ~= "WEAPON_GOLFCLUB"
    and weaponList[i].name ~= "WEAPON_BOTTLE"
    and weaponList[i].name ~= "WEAPON_SWITCHBLADE"
    and weaponList[i].name ~= "WEAPON_MOLOTOV"
    and weaponList[i].name ~= "WEAPON_HEAVYSNIPER"
    and weaponList[i].name ~= "WEAPON_ASSAULTRIFLE"
    and weaponList[i].name ~= "WEAPON_BULLPUPRIFLE"
    and weaponList[i].name ~= "WEAPON_CARBINERIFLE"
    and weaponList[i].name ~= "WEAPON_SPECIALCARBINE" then
      local ammo = GetAmmoInPedWeapon(playerPed, weaponHash)
      table.insert(elements, {label = weaponList[i].label, value = weaponList[i].name})
    end

  end

  ESX.UI.Menu.Open(
    'default', GetCurrentResourceName(), 'armory_put_weapon',
    {
      title    = _U('put_weapon_menu'),
      align    = 'bottom-right',
      elements = elements,
    },
    function(data, menu)

      menu.close()

      ESX.TriggerServerCallback('esx_jobg:addArmoryWeapon', function()
        OpenPutWeaponMenu()
      end, data.current.value)

    end,
    function(data, menu)
      menu.close()
    end
  )

end

function OpenBuyWeaponsMenu(station)

  --ESX.TriggerServerCallback('esx_jobg:getArmoryWeapons', function(weapons)

    local elements = {}

    for i=1, #Config.MafiaStations[station].AuthorizedWeapons, 1 do

      local weapon = Config.MafiaStations[station].AuthorizedWeapons[i]
      local count  = 0

      for i=1, #weapons, 1 do
        if weapons[i].name == weapon.name then
          count = weapons[i].count
          break
        end
      end

      table.insert(elements, {label = 'x' .. count .. ' ' .. ESX.GetWeaponLabel(weapon.name) .. ' $' .. weapon.price, value = weapon.name, price = weapon.price})

    end

    ESX.UI.Menu.Open(
      'default', GetCurrentResourceName(), 'armory_buy_weapons',
      {
        title    = _U('buy_weapon_menu'),
        align    = 'bottom-right',
        elements = elements,
      },
      function(data, menu)

        ESX.TriggerServerCallback('esx_jobg:buy', function(hasEnoughMoney)

          if hasEnoughMoney then
              OpenBuyWeaponsMenu(station)
              TriggerServerEvent('esx_jobg:giveItem', data.current.value,data.current.price)
            
          else
            ESX.ShowNotification(_U('not_enough_money'))
          end

        end, data.current.price)

      end,
      function(data, menu)
        menu.close()
      end
    )

  --end)

end
